// Helper function to get H2H record between two players
export const getH2HRecord = (player1: string, player2: string): { wins: number; losses: number } | null => {
  // Aggregate all matches between the two players
  let wins = 0;
  let losses = 0;

  h2hRecords.forEach((record) => {
    if (record.fighter === player1 && record.opponent === player2) {
      wins += record.wins;
      losses += record.losses;
    }
  });

  if (wins === 0 && losses === 0) {
    return null;
  }

  return { wins, losses };
};

// Get all individual matchups between two players with tournament context
export const getAllMatchupsBetween = (player1: string, player2: string): { winner: string; loser: string; tournament: string; season: string }[] => {
  const matchups: { winner: string; loser: string; tournament: string; season: string }[] = [];
  
  h2hRecords.forEach((record) => {
    if (record.fighter === player1 && record.opponent === player2 && record.wins > 0) {
      for (let i = 0; i < record.wins; i++) {
        matchups.push({ winner: player1, loser: player2, tournament: record.tournament, season: record.season });
      }
    } else if (record.fighter === player2 && record.opponent === player1 && record.wins > 0) {
      for (let i = 0; i < record.wins; i++) {
        matchups.push({ winner: player2, loser: player1, tournament: record.tournament, season: record.season });
      }
    }
  });
  
  // Sort by season descending
  return matchups.sort((a, b) => parseInt(b.season) - parseInt(a.season));
};
